"use strict";$(function(){$("#login").validate({rules:{username:{required:!0,minlength:5,maxlength:15},password:{required:!0,minlength:6,maxlength:20}},messages:{username:{required:"请输入昵称/邮箱/手机号码"},password:{required:"请输入长度在 6-20 之间的字符串"}},submitHandler:function(e){e=$(e).serialize();$.post("../server/login.php",e,null,"json").then(function(e){console.log(e),0===e.code?$(".login_error").removeClass("hide"):1===e.code&&(setCookie("nickname",e.nickname),window.location.href="../pages/index.html")})}})});